mapper.select:
SELECT COUNT(*) FROM BOARD

WHERE BO_MOTHER_NO IS NULL
AND ST_NO = #{stNo}

	AND BO_CATEGORY = #{boCategory}

AND BO_STATUS = 1



WHERE BO_MOTHER_NO = #{boMotherNo}
AND BO_STATUS = 1

SELECT B.BO_NO, B.ST_NO, B.MB_NO, B.BO_MOTHER_NO, B.BO_INSERTDATE, B.BO_TITLE, B.BO_CONTENTS, B.BO_CATEGORY, B.BO_STATUS, M.MB_NICKNAME, M.MB_PHOTO FROM BOARD B
JOIN MEMBER M ON B.MB_NO = M.MB_NO
WHERE BO_MOTHER_NO IS NULL AND B.ST_NO = #{stNo}
AND B.BO_CATEGORY = #{boCategory}
AND B.BO_STATUS = 1 ORDER BY B.BO_NO DESC

mapper.update:
UPDATE BOARD SET


BO_CATEGORY = #{boCategory}, BO_TITLE = #{boTitle},

BO_CONTENTS = #{boContents}

WHERE BO_NO = #{boNo}
